from gendiff.engine import generate_diffdef test_gendiff():    # simple test    timeout_result = generate_diff("tests/fixtures/old_timeout.json",                                   "tests/fixtures/new_timeout.json")    timeout_expect = "{\n+ timeout: 50\n- timeout: 20\n}"    # testing dict in dict    recursion_result = generate_diff("tests/fixtures/old_recur.json",                                     "tests/fixtures/new_recur.json")    recursion_expect = "{\n  recursion: {a: 42}\n}"    # simple YAML test    example_result = generate_diff("tests/fixtures/simple_old.json",                                   "tests/fixtures/simple_new.json")    example_expect = "{\n+ example: 10\n- example: 20\n}"    assert timeout_result == timeout_expect    assert recursion_result == recursion_expect    assert example_result == example_expect